mapper.insert:

	SELECT SEQ_DRAFT.NEXTVAL FROM DUAL


INSERT INTO DRAFT 
VALUES (
	 #{draftNo}
	,#{draftTitle}
	,#{draftContent}
	,#{comNo}
	,DEFAULT
)

mapper.select:
SELECT D.DRAFT_NO ,DRAFT_TITLE ,DRAFT_CONTENT ,COM_NO ,DRAFT_FLAG ,DECODE(DRAFT_FLAG, '2', '협조문', '3', '품의서', '4', '지출결의서','5', '경조사 신청서', '6', '회의록', '기안서') AS DRAFT_NM ,(CASE WHEN KEEP_NO IS NULL OR KEEP_YN = 'N' THEN 'N' ELSE 'Y' END) AS KEEP_YN FROM DRAFT D LEFT OUTER JOIN DRAFT_KEEP K ON D.DRAFT_NO = K.DRAFT_NO WHERE COM_NO = #{comNo}
AND DRAFT_FLAG = #{key}
AND DRAFT_FLAG IN ('1','7')
ORDER BY DRAFT_FLAG, DRAFT_NO

mapper.update:
UPDATE DRAFT SET
	 DRAFT_TITLE = #{draftTitle}
	,DRAFT_CONTENT = #{draftContent}
WHERE DRAFT_NO = #{draftNo}
