mapper.select:
SELECT COUNT(*)
FROM DEPARTMENT
WHERE COM_NO = #{comNo}

SELECT DEPT_NO, DEPT_NM
FROM DEPARTMENT
WHERE COM_NO = #{comNo}

SELECT TEAM_NO, TEAM_NM, DEPT_NO
FROM TEAM
WHERE DEPT_NO = #{deptNo}

SELECT COUNT(*)
FROM TEAM
WHERE DEPT_NO = #{deptNo}

SELECT CALENDAR_TITLE, CALENDAR_START, CALENDAR_END, CALENDAR_COLOR
FROM CALENDAR
WHERE COM_SHARE = #{comNo}
AND COM_NO = #{comNo}
