mapper.update:
UPDATE DRAFT_KEEP SET
	KEEP_YN = #{keepYn}
WHERE DRAFT_NO = #{draftNo}
AND EMP_CODE = #{empCode}

mapper.insert:

	SELECT SEQ_EDSM.NEXTVAL FROM DUAL


INSERT INTO EDSM
VALUES(
	 #{edsmNo}
	,#{edsmTitle}
	,#{edsmContent}
	,DEFAULT
	,DEFAULT
	,#{empCode}
	,DEFAULT
	,#{draftNo}
)

INSERT INTO EDSM_FILE


	SELECT
		 NEXT_EDSM_FILE()
		,#{file.filePath}
		,#{file.fileOriginName}
		,#{file.fileRename}
		,SYSDATE
		,#{file.fileOrder}
		,#{file.edsmNo}
	FROM DUAL

INSERT INTO APPROVER


	SELECT
		 NEXT_APPROVER()
		,#{approver.approverFlag}
		,#{approver.empCode}
		,NULL
		,NULL
		,#{approver.edsmNo}
		,NULL
	FROM DUAL

mapper.update:
UPDATE EDSM SET
	EDSM_DEL_FL = 'Y'
WHERE EDSM_NO = #{edsmNo}

UPDATE APPROVER SET
	PROGRESS_FLAG = '1'
WHERE EDSM_NO = #{edsmNo}
AND EMP_CODE = #{empCode}

UPDATE EDSM SET
	EDSM_FLAG = #{edsmFlag}
WHERE EDSM_NO = #{edsmNo}

UPDATE APPROVER SET
	 PROGRESS_FLAG = '2'
	,APPROVE_FLAG = '2'
	,REJECTED = #{rejected}
WHERE EDSM_NO = #{edsmNo}
AND EMP_CODE = #{empCode}
