mapper.select:
SELECT COUNT(*)
FROM "TB_AUTH_KEY"
WHERE EMAIL = #{email}
AND AUTH_KEY = #{authKey}

SELECT COUNT(*)
FROM EMPLOYEE
WHERE EMP_DEL_FL = 'N'
AND EMP_FIRST_NAME = #{empName}
AND EMP_EMAIL = #{empEmail}

SELECT COUNT(*)
FROM EMPLOYEE
WHERE EMP_DEL_FL = 'N'
AND EMP_ID = #{empId}
AND EMP_EMAIL = #{empEmail}
